#include<algorithm>#include<vector>#include<set>#include<queue>using namespace std;class SeatManager {	 int numsize;		 priority_queue<int,greater<int>,vector<int> > que;public:	SeatManager(int n) {		//返回  		numsize = n;		for (int i = 1; i <= n; i++) {			que.push(i);		}	}	//最小堆	int reserve() {			int retNum = que.top();		que.pop();		return retNum;	}	//插入一个可以预约	void unreserve(int seatNumber) {		que.push(seatNumber);	}};
